import math

import cv2
import numpy as np

lut = np.load("lut.npy")

y_max = 100
x_max = 250

points = np.zeros([y_max, x_max])

for y in range(y_max):
    y_n = y + 1
    for x in range(x_max):
        x_n = x + 1
        print(y_n, x_n)

        min_value = 9999
        for i in range(len(lut)):
            for j in range(len(lut[i])):

                if lut[i][j][0] > 0:
                    _, y2, x2 = lut[i][j]
                    x2 *= 25
                    y2 *= 10

                    y_norm = (y_n / y_max) * 10
                    x_norm = (x_n / x_max) * 25

                    dist = math.sqrt(math.pow(x2-x_norm, 2) + math.pow(y2-y_norm, 2))
                    if dist < min_value:
                        min_value = dist
        points[y][x] = min_value

print("D=np.array(" + str(points.tolist()) + ")")
